mapper.select:
select 

from equipment
where equip_id = #{equipId,jdbcType=VARCHAR}
  and equip_cab_id = #{equipCabId,jdbcType=VARCHAR}
  and mr_id = #{mrId,jdbcType=VARCHAR}

mapper.delete:
delete from equipment
where equip_id = #{equipId,jdbcType=VARCHAR}
  and equip_cab_id = #{equipCabId,jdbcType=VARCHAR}
  and mr_id = #{mrId,jdbcType=VARCHAR}
